import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import "./UserListModal.scss";

const UserListModal = (props) => {
  const [userList, setUserList] = useState([]);
  const UserList = useCallback(async () => {
    try {
      const response = await axios.get("/selectAll", {
        headers: {
          Authorization:
            "Bearer " +
            "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJjb3PthqDtgbAiLCJtX3NlcSI6IjQ3IiwiZXhwIjoxNjUyOTE3Njk3LCJ1c2VybmFtZSI6InRlc3QifQ.o6tNroJidXin8g9lqwKtD6DEpUBpWIWvR5OwJhkRCNstlz85PuFoQT5AHampCiV3f_K7Hz_btt4eOVqNZCg4Fw",
        },
      });
      setUserList(response.data);
    } catch (error) {
      console.log(error);
    }
  }, []);

  useEffect(() => {
    UserList();
  }, [UserList]);


  const deleteUser = () => {

  };

  return (
    <div className="userListModal">
      <div className="userModalOverlay"></div>
      <div className="closeBtn">
        <svg
          aria-label="닫기"
          className="_8-yf5 "
          color="#ffffff"
          fill="#ffffff"
          height="24"
          role="img"
          viewBox="0 0 24 24"
          width="24"
        >
          <polyline
            fill="none"
            points="20.643 3.357 12 12 3.353 20.647"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="3"
          ></polyline>
          <line
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="3"
            x1="20.649"
            x2="3.354"
            y1="20.649"
            y2="3.354"
          ></line>
        </svg>
      </div>
      <div className="userModalContent">
        <div className="userListTitle">사용자 정보</div>
        <div className="userListContent">
          <table className="userListTable">
            <thead className="userListHead">
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Tell</th>
                <th>Email</th>
                <th>Sign-up Date</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {userList !== undefined &&
                userList.map((data) => (
                  <tr key={data.m_seq} className="userListInfo">
                    <td>{data.m_id}</td>
                    <td>{data.m_name}</td>
                    <td>{data.m_tell}</td>
                    <td>{data.m_email}</td>
                    <td>{data.m_date}</td>
                    <td className="userDelBtn">삭제</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UserListModal;
