import axios from "axios";
import React from "react";

import "./SelectModal.scss";

const SelectModal = (props) => {
  console.log(localStorage.getItem("jwtToken"));
  const { cancelModalHandler, data } = props;

  const boardDelete = async (delData) => {
    try {
      const response = await axios.delete("/deleteBoard", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwtToken"),
        },
        data: {"b_seq":b_seq}
      });
      return response;
    } catch(error) {
      console.log("error", error);
      return null;
    }
  };

  const delBtnHandler = () => {
    boardDelete(data.b_seq);
    cancelModalHandler();
  }

  return (
    <div className="modal1">
      <div className="modalOverlay1" onClick={cancelModalHandler}></div>
      <div className="modalContent1">
        <div className="selectModalWrap">
          <button className="selectBtn del" onClick={delBtnHandler}>삭제</button>
          <button className="selectBtn">수정</button>
          <button className="selectBtn" onClick={cancelModalHandler}>
            취소
          </button>
        </div>
      </div>
    </div>
  );
};

export default SelectModal;
